import Header from './components/Header';
import Checklist from './components/Checklist';
import WeatherTip from './components/WeatherTip';
import ItemCard from './components/ItemCard';
import DustCard from './components/DustCard';
import UVCard from './components/UVCard';

function App() {
  const checklistItems = [
    "우산을 챙기세요!",
    "자외선 차단제를 바르세요!",
    "마스크를 챙기세요!"
  ];

  const fineDust = 47;
  const ultraFineDust = 23;
  const uvIndex = 6.4;

  return (
    <div style={{ padding: '1rem', fontFamily: 'sans-serif' }}>
      <Header location="이화여대" temp={16} max={24} min={12} />
      <Checklist items={checklistItems} />
      <WeatherTip text="비가 오는 날에는 해물파전을 먹어보면 어떨까요?" />

      {/* 카드 그리드 */}
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
        gap: '1rem',
        marginTop: '1rem'
      }}>
        <div style={{ flexBasis: '48%' }}>
          <ItemCard title="오늘의 공구템 추천">
            <p>학잠 0온스</p>
          </ItemCard>
        </div>

        <div style={{ flexBasis: '48%' }}>
          <DustCard fine={fineDust} ultraFine={ultraFineDust} />
        </div>

        <div style={{ flexBasis: '48%' }}>
          <ItemCard title="강수량">
            <p>강수 확률 20%</p>
            <p>강수량 50mm</p>
            <p style={{ fontSize: '0.85rem', color: '#555' }}>{'>'} 교내 우산 대여 안내</p>
          </ItemCard>
        </div>

        <div style={{ flexBasis: '48%' }}>
          <UVCard uvIndex={uvIndex} />
        </div>
      </div>
    </div>
  );
}

export default App;
