import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Weather.css';

import TimeTempToggle from './TimeTempToggle';
import Checklist from './Checklist';
import WeatherTip from './WeatherTip';
import ItemCard from './ItemCard';
import DustCard from './DustCard';
import UVCard from './UVCard';
import BusCard from './BusCard';

const Weather = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get('/api/weather') // Flask에서 이 주소로 JSON 응답
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <div>로딩 중...</div>;

  return (
    <div className="weather-container">
      <TimeTempToggle location={data.location} temperature={data.temperature} />
      <Checklist items={data.checklist} />
      <WeatherTip tip={data.tip} />
      <ItemCard item={data.item} />
      <DustCard dust={data.dust} />
      <UVCard uv={data.uv} />
      <BusCard schedule={data.bus} />
    </div>
  );
};

export default Weather;
