import React, { useState } from 'react';

function TimeTempToggle() {
  const [showTemp, setShowTemp] = useState(false);

  const handleToggle = () => {
    setShowTemp(prev => !prev);
  };

  return (
    <button className="button-rounded" onClick={handleToggle}>
      {showTemp ? '16° (현재 기온)' : '오늘의 실시간 기온 예보 확인'}
    </button>
  );
}

export default TimeTempToggle;
