import ItemCard from './ItemCard';
import { getFineDustLevel, getUltraFineDustLevel } from '../utils/airQualityUtils';

const DustBox = ({ label, value, imageSrc, level }) => (
  <div style={{ textAlign: 'center' }}>
    <img src={imageSrc} alt={label} width={60} />
    <p style={{ fontWeight: 'bold' }}>{label}</p>
    <p style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
      {value} <span style={{ fontSize: '0.8rem' }}>㎍/㎥</span>
    </p>
    <p style={{ fontSize: '0.9rem', color: level.color }}>{level.label}</p>
    <div style={{
      width: '100px',
      height: '8px',
      backgroundColor: '#eee',
      borderRadius: '4px',
      overflow: 'hidden',
      margin: '0.3rem auto'
    }}>
      <div style={{
        width: `${Math.min(value, 200) / 2}%`,
        height: '100%',
        backgroundColor: level.color
      }} />
    </div>
  </div>
);

const DustCard = ({ fine, ultraFine }) => {
  const fineLevel = getFineDustLevel(fine);
  const ultraLevel = getUltraFineDustLevel(ultraFine);

  return (
    <ItemCard title="미세먼지 정보">
      <div style={{
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center'
      }}>
        <DustBox
          label="미세먼지"
          value={fine}
          level={fineLevel}
          imageSrc="/images/logo512.png"
        />
        <DustBox
          label="초미세먼지"
          value={ultraFine}
          level={ultraLevel}
          imageSrc="/images/logo512.png"
        />
      </div>
    </ItemCard>
  );
};

export default DustCard;
