import React, { useEffect, useState } from 'react';
//import axios from 'axios';
import './Weather.css';

import TimeTempToggle from './TimeTempToggle';
import Checklist from './Checklist';
import WeatherTip from './WeatherTip';
import ItemCard from './ItemCard';
import DustCard from './DustCard';
import UVCard from './UVCard';
import BusCard from './BusCard';

const Weather = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const mockData = {
      location: "이화여대",
      temperature: { current: 16, high: 24, low: 12 },
      checklist: ["우산을 챙기세요!", "자외선 차단제를 바르세요!", "마스크를 챙기세요!"],
      tip: "비가 오는 날에는 해물파전을 먹어보면 어떨까요?",
      item: { name: "학잠 O온스", image: "/images/jacket.png" },
      dust: {
        pm10: { grade: "나쁨", value: 47 },
        pm25: { grade: "보통", value: 23 }
      },
      uv: { index: 0, level: "낮음" },
      bus: {
        연합행: "7:50 ~ 21:00",
        한우리행: "8:25 ~ 16:05",
        야간기숙사: "21:10 ~ 23:40",
        점심시간제외: "12:00 ~ 13:00"
      }
    };
    setData(mockData);
  }, []);

  /*
  useEffect(() => {
    axios.get('/api/weather') // Flask에서 이 주소로 JSON 응답
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);
*/
  if (!data) return <div>로딩 중...</div>;

  return (
    <div className="card-grid">
      <ItemCard item={data.item} />
      <DustCard dust={data.dust} />
      <UVCard uv={data.uv} />
      <BusCard schedule={data.bus} />
    </div>


  );
};

export default Weather;
