import ItemCard from './ItemCard';
import { getUVLevel } from '../utils/uvUtils';

const UVCard = ({ uvIndex }) => {
  const uv = getUVLevel(uvIndex);

  return (
    <ItemCard title="자외선 지수">
      <div style={{ textAlign: 'center' }}>
        <p style={{ fontSize: '2rem' }}></p>
        <p style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>{uvIndex}</p>
        <p style={{ fontSize: '1rem', color: uv.color }}>{uv.label}</p>
        <div style={{
          width: '100px',
          height: '8px',
          backgroundColor: '#eee',
          borderRadius: '4px',
          overflow: 'hidden',
          margin: '0.5rem auto'
        }}>
          <div style={{
            width: `${(uvIndex / 11) * 100}%`,
            height: '100%',
            backgroundColor: uv.color
          }} />
        </div>
      </div>
    </ItemCard>
  );
};

export default UVCard;
