import './App.css';
import './components/Checklist.css';
import './components/DustCard.css';
import './components/Weather';

import Weather from './components/Weather';
import Checklist from './components/Checklist';
import WeatherTip from './components/WeatherTip';
import ItemCard from './components/ItemCard';
import DustCard from './components/DustCard';
import UVCard from './components/UVCard';
import BusCard from './components/BusCard';
import TimeTempToggle from './components/TimeTempToggle';

function App() {
  const checklistItems = [
    "우산을 챙기세요!",
    "자외선 차단제를 바르세요!",
    "마스크를 챙기세요!"
  ];

  const fineDust = 47;
  const ultraFineDust = 23;
  const uvIndex = 0;

  return (
    <div className="App">
      <Weather location="이화여대" temp={16} max={24} min={12} />

      <Checklist items={checklistItems} />

      <div className="weather-tip">
        비가 오는 날에는 해물파전을 먹어보면 어떨까요?
      </div>

      <div className="card-grid">
        <div className="left-column">
          <ItemCard title="오늘의 공구템 추천">
            <img src="/images/jacket.png" alt="학잠" width="80" />
            <p>학잠 0온스</p>
          </ItemCard>

          <ItemCard title="강수량">
            <p>강수 확률 20%</p>
            <p>강수량 50mm</p>
            <p className="card-footer">
              {'>'} 교내 우산 대여 서비스 안내 {'<'}<br />
              {'>'} 비오는 날 위험한 길은? {'<'}
            </p>
          </ItemCard>

          <UVCard uvIndex={uvIndex} />
        </div>

        <div className="right-column">
          <DustCard fine={fineDust} ultraFine={ultraFineDust} />
          <BusCard />
        </div>
      </div>

      <TimeTempToggle />
      <button className="button-rounded">대피소 위치 확인하기</button>
    </div>
  );
}

export default App;
